const error = {
    instancePath: "",
    message: "match rule should be either a string or an object",
};
isInvalidRule(null, error);
isInvalidRule(undefined, error);
isInvalidRule(10, error);
isInvalidRule([12, 24], error);

isInvalidRule({
        and: [{ or: [{ pattern: "CS2103T", info2: "foobar" }], abc: "def" }],
        mno: "pqr",
    }, {
        instancePath: "/and/0/or/0",
        message: "pattern match rule should have properties 'pattern', 'exclude' (optional) and 'info' (optional) only",
    }, {
        instancePath: "/and/0",
        message: "or match rule should have property 'or' only",
    }, {
        instancePath: "",
        message: "and match rule should have property 'and' only",
    })

const message = "match rule should have only one of properties 'pattern', 'and', 'or'";
isInvalidRule({
    or: [
        { pattern: "CS1231", and: [] },
        { pattern: "CS1231", or: [] },
        { and: [{ and: ["abc"], or: ["def"] }] },
    ],
}, { instancePath: "/or/0", message }, { instancePath: "/or/1", message }, { instancePath: "/or/2/and/0", message });

const message = "pattern should be a non-empty string composed of A-Z, 0-9, x or *";
isInvalidRule({
    and: [
        "CS210c",
        { pattern: "CS2100", exclude: "CS1231s" },
        { pattern: "CS210#T", exclude: ["CS1231", "CS21%"] },
    ],
}, { instancePath: "/and/0", message }, { instancePath: "/and/1/exclude", message }, { instancePath: "/and/2/pattern", message }, { instancePath: "/and/2/exclude/1", message });

const message = "property 'exclude' should be either a pattern or an array of patterns";
isInvalidRule({
    or: [
        { pattern: "CS2101", exclude: {} },
        { pattern: "CS2101", exclude: 5 },
    ],
}, { instancePath: "/or/0/exclude", message }, { instancePath: "/or/1/exclude", message });

