isCorrectFilter(filter, modulesList1, modulesList1.map(([moduleStr]) => moduleStr));

const satisfier = satisfiers_1.inequalitySatisfier(ref, "<=32");
satisfier.should.have.property("filter");
const { filter } = satisfier;
isCorrectFilter(filter, modulesList1, modulesList1.map(([moduleStr]) => moduleStr));

const satisfier = satisfiers_1.inequalitySatisfier(ref, "<=16");
satisfier.should.have.property("filter");
const { filter } = satisfier;
isCorrectFilter(filter, modulesList1, [
    "CS2100",
    "GER1000",
    "CS2040S",
    "ST2131",
]);

const satisfier = satisfiers_1.inequalitySatisfier(ref, "<=17");
satisfier.should.have.property("filter");
const { filter } = satisfier;
isCorrectFilter(filter, modulesList1, [
